generate_id_token(missing_field, ClientId, Nonce, Issuer) ->
    ClaimSetMap =
        #{iss => Issuer,
          aud => [ClientId, <<"someotherid">>],
          nonce => Nonce,
          sub => <<"joe">>},
    generate_id_token(ClaimSetMap, 600);
generate_id_token(valid_group, ClientId, Nonce, Issuer) ->
    ClaimSetMap =
        #{iss => Issuer,
          aud => [ClientId, <<"someotherid">>],
          azp => ClientId,
          nonce => Nonce,
          sub => <<"joe">>,
          iat => 123},
    generate_id_token(ClaimSetMap, 600);
generate_id_token(valid, ClientId, Nonce, Issuer) ->
    ClaimSetMap =
        #{iss => Issuer,
          aud => ClientId,
          nonce => Nonce,
          sub => <<"joe">>,
          iat => 123},
    generate_id_token(ClaimSetMap, 600);
generate_id_token(expired, ClientId, Nonce, Issuer) ->
    ClaimSetMap =
        #{iss => Issuer,
          aud => ClientId,
          nonce => Nonce,
          sub => <<"joe">>,
          iat => 123},
    generate_id_token(ClaimSetMap, -600);
generate_id_token(bad_algo, ClientId, Nonce, Issuer) ->
    ClaimSetMap =
        #{iss => Issuer,
          aud => ClientId,
          nonce => Nonce,
          sub => <<"joe">>,
          iat => 123},
    Key = #{kty => <<"oct">>, k => <<"some shared secret">>},
    erljwt:create(hs256, ClaimSetMap, 600, Key).

introspect_test() ->
    Token1 =
        <<"{\"active\":true,\"scope\":\"openid profile\",\"client_id\":\"id1\", \"username\":\"joe\", \"exp\":234}">>,
    Exp1 =
        #{active => true,
          scope => #{scope => <<"openid profile">>, list => [<<"openid">>, <<"profile">>]},
          username => <<"joe">>,
          exp => 234,
          client_id => #{id => <<"id1">>, same => true}},
    Token2 = <<"{\"active\":true}">>,
    Exp2 =
        #{active => true,
          scope => #{scope => <<"">>, list => []},
          username => undefined,
          exp => undefined,
          client_id => #{id => undefined, same => false}},
    ?assertEqual(Exp1, oidcc_token:introspect_token_map(Token1, <<"id1">>)),
    ?assertEqual(Exp2, oidcc_token:introspect_token_map(Token2, <<"id1">>)),
    ok.

mock_oidcc(OpenIdProviderId, Issuer, ClientId) ->
    application:set_env(erljwt, add_iat, false),
    Encode =
        fun(Int) ->
           base64url:encode(
               binary:encode_unsigned(Int))
        end,
    [E, N] = ?RSA_PUBLIC_KEY,
    InfoFun =
        fun(Id) ->
           Id = OpenIdProviderId,
           {ok,
            #{issuer => Issuer,
              client_id => ClientId,
              keys =>
                  [#{kty => <<"RSA">>,
                     n => Encode(N),
                     e => Encode(E)}]}}
        end,
    SelfFun = fun(_) -> {ok, self()} end,
    KeysFun = fun(_) -> {ok, [#{kty => <<"oct">>, k => <<"some shared secret">>}]} end,
    ok = meck:new(oidcc),
    ok = meck:new(oidcc_openid_provider_mgr),
    ok = meck:new(oidcc_openid_provider),
    ok = meck:expect(oidcc, get_openid_provider_info, InfoFun),
    ok = meck:expect(oidcc_openid_provider_mgr, get_openid_provider, SelfFun),
    ok = meck:expect(oidcc_openid_provider, update_and_get_keys, KeysFun),
    ok.

stop_mocking_oidcc() ->
    true = meck:validate(oidcc),
    true = meck:validate(oidcc_openid_provider),
    true = meck:validate(oidcc_openid_provider_mgr),
    meck:unload(oidcc),
    meck:unload(oidcc_openid_provider),
    meck:unload(oidcc_openid_provider_mgr),
    application:unset_env(erljwt, add_iat),
    ok.

generate_id_token(ClaimSetMap, Expiration) ->
    erljwt:create(rs256, ClaimSetMap, Expiration, to_key(?RSA_PRIVATE_KEY)).

to_key(#'RSAPrivateKey'{modulus = Modulus,
                        publicExponent = PubExp,
                        privateExponent = PrivExp}) ->
    E = base64url:encode(
            binary:encode_unsigned(PubExp)),
    N = base64url:encode(
            binary:encode_unsigned(Modulus)),
    D = base64url:encode(
            binary:encode_unsigned(PrivExp)),
    #{kty => <<"RSA">>,
      n => N,
      e => E,
      d => D}.
